/**
 * The alignment or justification for a paragraph of text
 *
 * @since CS2
 * @version CS2
 *
 * <b>enum</b>
 * ･ {@link CENTER}
 * ･ {@link FULLJUSTIFY}
 * ･ {@link FULLJUSTIFYLASTLINECENTER}
 * ･ {@link FULLJUSTIFYLASTLINELEFT}
 * ･ {@link FULLJUSTIFYLASTLINERIGHT}
 * ･ {@link LEFT}
 * ･ {@link RIGHT}
 */
declare class Justification
{
	/**
	 * @static
	 * @const
	 * @type {Justification}
	 * @see {@link Justification}
	 * @since CS2
	 * @version CS2
	 */
	static readonly CENTER:Justification;

	/**
	 * @static
	 * @const
	 * @type {Justification}
	 * @see {@link Justification}
	 * @since CS2
	 * @version CS2
	 */
	static readonly FULLJUSTIFY:Justification;

	/**
	 * @static
	 * @const
	 * @type {Justification}
	 * @see {@link Justification}
	 * @since CS2
	 * @version CS2
	 */
	static readonly FULLJUSTIFYLASTLINECENTER:Justification;

	/**
	 * @static
	 * @const
	 * @type {Justification}
	 * @see {@link Justification}
	 * @since CS2
	 * @version CS2
	 */
	static readonly FULLJUSTIFYLASTLINELEFT:Justification;

	/**
	 * @static
	 * @const
	 * @type {Justification}
	 * @see {@link Justification}
	 * @since CS2
	 * @version CS2
	 */
	static readonly FULLJUSTIFYLASTLINERIGHT:Justification;

	/**
	 * @static
	 * @const
	 * @type {Justification}
	 * @see {@link Justification}
	 * @since CS2
	 * @version CS2
	 */
	static readonly LEFT:Justification;

	/**
	 * @static
	 * @const
	 * @type {Justification}
	 * @see {@link Justification}
	 * @since CS2
	 * @version CS2
	 */
	static readonly RIGHT:Justification;
}