/**
 * Types for fonts included in exported SVG files
 *
 * @since CS2
 * @version CS2
 *
 * <b>enum</b>
 * ･ {@link CEFFONT}
 * ･ {@link SVGFONT}
 * ･ {@link OUTLINEFONT}
 */
declare class SVGFontType
{
	/**
	 * @static
	 * @const
	 * @type {SVGFontType}
	 * @see {@link SVGFontType}
	 * @since CS2
	 * @version CS2
	 */
	static readonly CEFFONT:SVGFontType;

	/**
	 * @static
	 * @const
	 * @type {SVGFontType}
	 * @see {@link SVGFontType}
	 * @since CS2
	 * @version CS2
	 */
	static readonly OUTLINEFONT:SVGFontType;

	/**
	 * @static
	 * @const
	 * @type {SVGFontType}
	 * @see {@link SVGFontType}
	 * @since CS2
	 * @version CS2
	 */
	static readonly SVGFONT:SVGFontType;
}